import { createSlice } from "@reduxjs/toolkit";

const initialstate = {
    value:0,
}

export const CounterSlice = createSlice({
    name:"counter",
    initialstate,
    reducers: {
        increment: (state)=>{
            state.value += 1;
        },
        drecement: (state)=>{
            state.value -=1;
        },
    }
})

export const {increment,drecement}=CounterSlice.actions;
export default CounterSlice.reducers;
